            setState2({
                ...state,
                H:0,
                M:0,
                S:0
            })
        }
        else {
            setState2({
                ...state,
                H:H,
                M:M,
                S:S
            })
        }
    }
        setInterval(timeSaleFn, 1000)
    }, [state2.H, state2.M, state2.S])
    const [product, setProduct] = React.useState([]);
